use rhdl::prelude::*;

#[derive(Circuit, Clone)]
pub struct AndGate;

impl CircuitDQ for AndGate {
    type D = (); 
    type Q = (); 
}

impl CircuitIO for AndGate {
    type I = Signal<(bool, bool), Red>;
    type O = Signal<bool, Red>;
    type Kernel = and_gate;
}

#[kernel]
pub fn and_gate(i: Signal<(bool, bool), Red>, _q: ()) -> (Signal<bool, Red>, ()) {
    let (a, b) = i.val();
    let c = a & b;
    (signal(c), ())
}
