#[derive(PartialEq, Clone, Copy, Digital)]
pub struct Bar(b8, b8);

#[derive(PartialEq, Clone, Copy, Digital)]
pub struct Foo {
    a: b8,
    b: Bar,
}

#[kernel]
pub fn kernel(state: Signal<Foo, Red>) -> Signal<b8, Red> {
    let Foo { a, b: Bar(_x, y) } = state.val();
    signal((a + y).resize())
}
