#[kernel]
pub fn xnor<const N: usize>(a: Bits::<N>, b: Bits::<N>) -> Bits::<N> 
where rhdl::bits::W<N>: BitWidth
{
    let mut ret_value = bits::<N>(0);
    for i in 0..N {
        let a_bit = a & (1 << i) != 0;
        let b_bit = b & (1 << i) != 0;
        if !(a_bit ^ b_bit) {
            ret_value |= (1 << i);
        }
    }
    ret_value
}

#[kernel]
pub fn kernel(a: b8, b: b8) -> b8 {
    xnor::<8>(a,b)
}
