#[derive(PartialEq, Debug, Digital, Default, Clone, Copy)]
pub enum SimpleEnum {
    #[default]
    Init,
    Run(b8),
    Point {
        x: b4,
        y: b8,
    },
    Boom,
}

const B6: b6 = bits(6);

#[kernel]
fn kernel(state: SimpleEnum) -> b8 {
    match state {
        SimpleEnum::Init => bits(1),
        SimpleEnum::Run(x) => x,
        SimpleEnum::Point { x: _, y } => y,
        SimpleEnum::Boom => bits(7),
    }
}
